settings.cardCss:
.card {
   width: 100%;
   height: 100%;
   display: flex;
   justify-content: center;
   align-items: center;
   gap: 20px;
}

.card .content {
   display: flex;
   flex-direction: row;
   gap: 20px;
   justify-content: center;
   width: 100%;
}

.card .column h1 {
    text-transform: uppercase;
    font-size: 18px;
    margin: 0 0 5px 0;
}

.card .column .value {
    font-size: 28px;
    margin: 5px 0;
    font-weight: bold;
}
